INSERT INTO `bb_posts` VALUES(1, 1, 1, 1, '<p>First Post!  w00t.\n</p>\n', '2009-10-20 11:43:13', '127.0.0.1', 1, 1);
INSERT INTO `bb_posts` VALUES(2, 1, 2, 1, '<p>Är det någon mer än jag som tycker detta är rätt konsistens?\n</p>\n', '2009-10-26 12:58:43', '127.0.0.1', 1, 1);
INSERT INTO `bb_posts` VALUES(3, 1, 1, 1, '<p>Tjoho.\n</p>\n', '2009-10-26 13:26:54', '127.0.0.1', 1, 2);
INSERT INTO `bb_posts` VALUES(4, 9, 3, 1, '<p>Dag 1 Kväll:<br />\n100 gram vetesurdegsgrund<br />\n200 gram ljummet vatten<br />\n50 gram fint rågmjöl<br />\n150 gram vetemjöl special</p>\n<p>Dag 2 morgon:<br />\nSurdegen från gårdagen<br />\n750 gram vetemjöl special<br />\n450 gram vatten<br />\n25 gram salt\n</p>\n', '2009-10-26 14:03:33', '127.0.0.1', 1, 1);
INSERT INTO `bb_posts` VALUES(5, 2, 4, 2, '<p>Vore bra att få det mesta översatt här också.\n</p>\n', '2009-10-31 07:46:24', '83.227.145.158', 1, 1);
INSERT INTO `bb_posts` VALUES(6, 10, 5, 3, '<p>Tjena!</p>\n<p>Jag skulle vilja ha en FAQ för nybörjare som vill komma igång med sitt surdegsbakande! </p>\n<p>/Chrille\n</p>\n', '2009-11-02 14:47:43', '85.225.11.56', 0, 1);
INSERT INTO `bb_posts` VALUES(7, 10, 5, 1, '<p>Hej Chrille!</p>\n<p>Vad kul att just du blev den första att skriva ett inlägg hos oss på riktigtbrod.se :) En FAQ är en utmärkt idé, man skulle  kunna tänka sig att allteftersom forumaktiviteten ökar här kan man skapa en FAQ-sida på sajten som bygger på medlemmarnas inlägg. Jag skulle kunna tänka mig att här ge dig en "kickstart" till ditt surdegsbakande som ett första steg.</p>\n<p>Det första du behöver göra är att starta en surdeg. Ett annat alternativ är att fråga någon som har en om du kan få lite av den. </p>\n<p>Till en nybörjare skulle jag rekommendera att starta en surdeg på rågmjöl då den tenderar att vara lite lättare att få fart på.</p>\n<p>Gör så här:<br />\n1. blanda en dl rågmjöl med en dl ljummet vatten i en mycket väl urdiskad bunke (detta är viktigt), plasta in och ställ undan (helst varmare än rumstemperatur dock max 28 grader), tex ett golv med värmeslingor, eller skåpet ovanför kylen.</p>\n<p>2. Efter två dygn bör smeten börjat skumma sig, häll bort hälften och tillsätt på nytt en dl rågmjöl och en dl ljummet vatten. Låt stå ytterligare ett dygn och upprepa processen en gång till.</p>\n<p>3. Surdegen bör nu vara aktiv och klar att bakas med.</p>\n<p>4. Om du inte avser att baka med den direkt, häll över den i en glasburk, jag använder en gammaldags konservburk (låt den vara max halvfull) och ställ in den i kylen.</p>\n<p>5. Ta ur surdegen en gång per vecka, häll ut hälften av innehållet och fyll på med en dl rågmjöl och en dl ljummet vatten, blanda, låt stå ute över natten och ställ in i kylen igen.\n</p>\n', '2009-11-02 15:15:07', '193.183.156.17', 0, 2);
INSERT INTO `bb_posts` VALUES(8, 2, 6, 1, '<p>Läste att Hornbach och Bauhaus säljer granitstenar i storleken 40*40*3 som endast kostar 49 kronor. Låter som en utmärkt investering för den som saknar sten i ugnen.\n</p>\n', '2009-11-02 15:45:05', '193.183.156.17', 0, 1);
INSERT INTO `bb_posts` VALUES(9, 10, 7, 1, '<p>...är perfekt att använda när man skall hälla in vatten på plåten under stenen i ugnen i samband med att brödet sätts in.\n</p>\n', '2009-11-08 10:32:24', '83.227.145.158', 0, 1);
INSERT INTO `bb_posts` VALUES(10, 2, 8, 4, '<p>Jag tror att vi har en stenugn på landet. Hur vet man om den går att använda för brödbak?\n</p>\n', '2009-11-08 11:25:45', '83.248.226.131', 0, 1);
INSERT INTO `bb_posts` VALUES(11, 2, 8, 1, '<p>Den går med all sannolikhet att använda. Vi provar i vår vettja :)\n</p>\n', '2009-11-08 12:15:46', '83.227.145.158', 0, 2);

